import { useState } from "react";
import Expenses from "./components/Expenses";
import NewExpense from "./components/NewExpense";

function App() {
  const DUMMY_EXPENSES = [
    { title: "Product 1", price: 10, date: new Date("02-24-2023"), category: "food" },
    { title: "Product 2", price: 20, date: new Date("04-05-2023"), category: "shopping" },
    { title: "Product 3", price: 45, date: new Date("06-01-2023"), category: "entertainment" },
  ];

  const [expenses, setNewExpense] = useState(DUMMY_EXPENSES);
  const addExpenseHandeler = (newExpese) => {
    setNewExpense((prevExpenses) => {
      return [newExpese, ...prevExpenses];
    });
  };

  return (
    <div className="main">
      <NewExpense data={expenses} onAddExpense={addExpenseHandeler} />
      <Expenses data={expenses}></Expenses>
    </div>
  );
}

export default App;
