import { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import AddExpenseButton from "./AddExpenseButton";

const NewExpense = (props) => {
  const [isAdding, setIsAdding] = useState(true);
  const btnAction = (value) => {
    setIsAdding(value);
  };

  const addExpenseOnSave = (newEnterdExpense) => {
    const expenseData = {
      ...newEnterdExpense,
    };
    props.onAddExpense(expenseData);

    setIsAdding(true);
    console.log("done");
  };

  return (
    <Card className={"add-new-expense-container"}>
      isAdding ? <AddExpenseButton onBtnClick={btnAction} /> : <ExpenseForm onBtnClick={btnAction} onExpenseSave={addExpenseOnSave} />;
    </Card>
  );
};

export default NewExpense;
