import { useState } from "react";
import Card from "./Card";

const ExpenseForm = (props) => {
  const [newExpense, setNewExpense] = useState("");
  const [newTitle, setNewTitle] = useState("");
  const [newDate, setNewDate] = useState("");

  const expenseChangeHandeler = (e) => {
    setNewExpense(e.target.value);
  };

  const titleChangeHandeler = (e) => {
    setNewTitle(e.target.value);
  };

  const dateChangeHandeler = (e) => {
    setNewDate(e.target.value);
  };

  const categoryChangeHandeler = (e) => {
    setNewCategory(e.target.value);
  };

  const submitHandeler = (e) => {
    e.preventDefault();
    const newExpenseData = {
      title: newTitle,
      price: newExpense,
      date: new Date(newDate),
      category: newCategory,
    };

    props.onExpenseSave(newExpenseData);
    setNewDate("");
    setNewExpense("");
    setNewTitle("");
    setNewTitle("");
  };

  return (
    <Card className="">
      <form className="new-expense-form" onSubmit={submitHandeler}>
        <div className="new-expense-controler">
          <input type="number" className="new-expense-amount" value={newExpense} onChange={expenseChangeHandeler} min="0.01" step="0.01" placeholder="$0" required />
        </div>
        <div className="new-expense-controler">
          <input type="text" className="new-expense-title" value={newTitle} onChange={titleChangeHandeler} placeholder="Title" required />
        </div>
        <div className="new-expense-controler">
          <input type="date" className="new-expense-date" value={newDate} onChange={dateChangeHandeler} required />
        </div>

        <select required>
          <option disabled selected hidden value="">
            Choose Category
          </option>
          <option value="health">Health</option>
          <option value="food">Food</option>
          <option value="shopping">Shopping</option>
          <option value="entertainment">Entertainment</option>
          <option value="travel">Travel</option>
        </select>

        <div className="new-expense-button">
          <button type="submit">Add New Expense</button>
        </div>
      </form>
    </Card>
  );
};

export default ExpenseForm;
