import { useState } from "react";
import Card from "./Card";

const ExpenseForm = () => {
  return (
    <Card>
      <form className="new-expense-form">
        <div className="new-expense-controler">
          <input type="number" onChange={inputExpenseHandeler} step="0.01" placeholder="$0" />
        </div>
        <div className="new-expense-controler">
          <input type="text" onChange={inputTitleHandeler} placeholder="Title" />
        </div>
        <div className="new-expense-controler">
          <input type="date" />
        </div>

        <div className="new-expense-button">
          <button type="submit">Submit</button>
        </div>
      </form>
    </Card>
  );
};

export default ExpenseForm;
