import { useState } from "react";
import Card from "./Card";

const ExpenseForm = (props) => {
  const [newExpense, setNewExpense] = useState("");
  const [newTitle, setNewTitle] = useState("");
  const [newDate, setNewDate] = useState("");

  const expenseChangeHandeler = (e) => {
    setNewExpense(e.target.value);
  };

  const titleChangeHandeler = (e) => {
    setNewTitle(e.target.value);
  };

  const dateChangeHandeler = (e) => {
    setNewDate(e.target.value);
  };

  const submitHandeler = (e) => {
    e.preventDefault();
    const newExpenseData = {
      amount: newExpense,
      title: newTitle,
      date: new Date(newDate),
    };

    setNewDate("");
    setNewExpense("");
    setNewTitle("");
    props.data.push(newExpenseData);
    console.log(props.data);
  };

  return (
    <Card>
      <form className="new-expense-form" onSubmit={submitHandeler}>
        <div className="new-expense-controler">
          <input type="number" value={newExpense} onChange={expenseChangeHandeler} min="0.01" step="0.01" placeholder="$0" required />
        </div>
        <div className="new-expense-controler">
          <input type="text" value={newTitle} onChange={titleChangeHandeler} placeholder="Title" required />
        </div>
        <div className="new-expense-controler">
          <input type="date" value={newDate} onChange={dateChangeHandeler} required />
        </div>

        <div className="new-expense-button">
          <button type="submit">Submit</button>
        </div>
      </form>
    </Card>
  );
};

export default ExpenseForm;
